package com.example.media_reels.VideoPlayerPrimport android.app.Activityimport android.content.Contextimport io.flutter.plugin.common.StandardMessageCodecimport io.flutter.plugin.platform.PlatformViewimport io.flutter.plugin.platform.PlatformViewFactoryclass VideoPlayerFactory(private val activity: Activity) : PlatformViewFactory(StandardMessageCodec.INSTANCE) {    override fun create(context: Context, viewId: Int, args: Any?): PlatformView {        val creationParams = args as Map<*, *>        return VideoPlayerView(context, viewId, creationParams,activity)    }}